contracts/DisputeDAO.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title DisputeDAO
 * @notice Decentralized dispute resolution for P2P trades
 * @dev Implements 3-arbitrator voting system with staking and rewards
 * 
 * Tiered Resolution:
 * - Tier 0: Auto-resolution (handled off-chain, high confidence cases)
 * - Tier 1: Community arbitration (3 arbitrators vote, 1-4 hours)
 * - Tier 2: Admin review (core team, 24 hours)
 */
contract DisputeDAO is ReentrancyGuard, Ownable {
    using SafeERC20 for IERC20;

    // ============ State Variables ============

    IERC20 public immutable usdc;
    address public escrowContract;

    uint256 public minArbitratorStake = 500 * 1e6;    // 500 USDC
    uint256 public minArbitratorTrades = 50;
    uint256 public maxDisputeRateBps = 200;           // 2%
    uint256 public arbitratorRewardBps = 50;          // 0.5% of dispute amount
    uint256 public votingPeriod = 4 hours;
    uint256 public votesRequired = 3;

    // ============ Enums ============

    enum DisputeTier {
        Auto,
        Community,
        Admin
    }

    enum DisputeStatus {
        None,
        Open,
        Voting,
        Resolved,
        Escalated
    }

    // ============ Structs ============

    struct Arbitrator {
        uint256 stake;
        uint256 disputesResolved;
        uint256 correctVotes;
        uint256 totalVotes;
        uint256 totalEarned;
        bool isActive;
        uint256 registeredAt;
    }

    struct Vote {
        bool hasVoted;
        bool favorBuyer;      // true = buyer wins, false = seller wins
        string reasoning;
        uint256 votedAt;
    }

    struct Dispute {
        bytes32 escrowOrderId;
        address buyer;
        address seller;
        uint256 amount;
        DisputeTier tier;
        DisputeStatus status;
        string reason;
        bytes32 buyerEvidence;   // IPFS hash
        bytes32 sellerEvidence;  // IPFS hash
        address[3] selectedArbitrators;
        uint256 votesForBuyer;
        uint256 votesForSeller;
        bool finalDecision;      // true = buyer wins
        uint256 createdAt;
        uint256 votingDeadline;
        uint256 resolvedAt;
    }

    // ============ Mappings ============

    mapping(address => Arbitrator) public arbitrators;
    mapping(uint256 => Dispute) public disputes;
    mapping(uint256 => mapping(address => Vote)) public disputeVotes;

    uint256 public disputeCount;
    address[] public arbitratorList;

    // ============ Events ============

    event ArbitratorRegistered(address indexed arbitrator, uint256 stake);
    event ArbitratorRemoved(address indexed arbitrator);
    event DisputeCreated(uint256 indexed disputeId, bytes32 indexed escrowOrderId, DisputeTier tier);
    event ArbitratorsSelected(uint256 indexed disputeId, address[3] arbitrators);
    event VoteCast(uint256 indexed disputeId, address indexed arbitrator, bool favorBuyer);
    event DisputeResolved(uint256 indexed disputeId, bool buyerWins);
    event DisputeEscalated(uint256 indexed disputeId, DisputeTier newTier);
    event ArbitratorRewarded(address indexed arbitrator, uint256 amount);
    event ArbitratorPenalized(address indexed arbitrator, uint256 accuracyDropBps);

    // ============ Errors ============

    error InsufficientStake();
    error NotArbitrator();
    error AlreadyVoted();
    error VotingClosed();
    error VotingNotClosed();
    error DisputeNotFound();
    error InvalidStatus();
    error NotAuthorized();
    error InsufficientArbitrators();

    // ============ Constructor ============

    constructor(address _usdc, address _escrowContract) Ownable(msg.sender) {
        usdc = IERC20(_usdc);
        escrowContract = _escrowContract;
    }

    // ============ Arbitrator Management ============

    /**
     * @notice Register as an arbitrator by staking USDC
     * @param stakeAmount Amount to stake (min 500 USDC)
     */
    function registerArbitrator(uint256 stakeAmount) external nonReentrant {
        if (stakeAmount < minArbitratorStake) revert InsufficientStake();
        if (arbitrators[msg.sender].isActive) revert NotAuthorized();

        usdc.safeTransferFrom(msg.sender, address(this), stakeAmount);

        arbitrators[msg.sender] = Arbitrator({
            stake: stakeAmount,
            disputesResolved: 0,
            correctVotes: 0,
            totalVotes: 0,
            totalEarned: 0,
            isActive: true,
            registeredAt: block.timestamp
        });

        arbitratorList.push(msg.sender);

        emit ArbitratorRegistered(msg.sender, stakeAmount);
    }

    /**
     * @notice Withdraw stake and deactivate arbitrator status
     */
    function withdrawArbitratorStake() external nonReentrant {
        Arbitrator storage arb = arbitrators[msg.sender];
        if (!arb.isActive) revert NotArbitrator();

        uint256 amount = arb.stake;
        arb.stake = 0;
        arb.isActive = false;

        usdc.safeTransfer(msg.sender, amount);

        emit ArbitratorRemoved(msg.sender);
    }

    /**
     * @notice Add more stake as arbitrator
     */
    function addArbitratorStake(uint256 amount) external nonReentrant {
        Arbitrator storage arb = arbitrators[msg.sender];
        if (!arb.isActive) revert NotArbitrator();

        usdc.safeTransferFrom(msg.sender, address(this), amount);
        arb.stake += amount;
    }

    // ============ Dispute Management ============

    /**
     * @notice Create a new dispute (called by escrow contract or parties)
     */
    function createDispute(
        bytes32 escrowOrderId,
        address buyer,
        address seller,
        uint256 amount,
        string calldata reason,
        DisputeTier tier
    ) external nonReentrant returns (uint256) {
        // Only escrow contract or parties can create disputes
        if (msg.sender != escrowContract && msg.sender != buyer && msg.sender != seller) {
            revert NotAuthorized();
        }

        disputeCount++;
        uint256 disputeId = disputeCount;

        disputes[disputeId] = Dispute({
            escrowOrderId: escrowOrderId,
            buyer: buyer,
            seller: seller,
            amount: amount,
            tier: tier,
            status: DisputeStatus.Open,
            reason: reason,
            buyerEvidence: bytes32(0),
            sellerEvidence: bytes32(0),
            selectedArbitrators: [address(0), address(0), address(0)],
            votesForBuyer: 0,
            votesForSeller: 0,
            finalDecision: false,
            createdAt: block.timestamp,
            votingDeadline: 0,
            resolvedAt: 0
        });

        emit DisputeCreated(disputeId, escrowOrderId, tier);

        // For community tier, select arbitrators immediately
        if (tier == DisputeTier.Community) {
            _selectArbitrators(disputeId);
        }

        return disputeId;
    }

    /**
     * @notice Submit evidence for a dispute
     */
    function submitEvidence(
        uint256 disputeId,
        bytes32 evidenceHash
    ) external {
        Dispute storage dispute = disputes[disputeId];
        if (dispute.status != DisputeStatus.Open && dispute.status != DisputeStatus.Voting) {
            revert InvalidStatus();
        }

        if (msg.sender == dispute.buyer) {
            dispute.buyerEvidence = evidenceHash;
        } else if (msg.sender == dispute.seller) {
            dispute.sellerEvidence = evidenceHash;
        } else {
            revert NotAuthorized();
        }
    }

    /**
     * @notice Start voting phase (after evidence submission)
     */
    function startVoting(uint256 disputeId) external {
        Dispute storage dispute = disputes[disputeId];
        if (dispute.status != DisputeStatus.Open) revert InvalidStatus();
        if (dispute.tier != DisputeTier.Community) revert InvalidStatus();

        dispute.status = DisputeStatus.Voting;
        dispute.votingDeadline = block.timestamp + votingPeriod;
    }

    /**
     * @notice Cast vote on a dispute (arbitrators only)
     */
    function voteOnDispute(
        uint256 disputeId,
        bool favorBuyer,
        string calldata reasoning
    ) external nonReentrant {
        Dispute storage dispute = disputes[disputeId];

        if (dispute.status != DisputeStatus.Voting) revert InvalidStatus();
        if (block.timestamp > dispute.votingDeadline) revert VotingClosed();
        if (!_isSelectedArbitrator(disputeId, msg.sender)) revert NotArbitrator();
        if (disputeVotes[disputeId][msg.sender].hasVoted) revert AlreadyVoted();

        // Record vote
        disputeVotes[disputeId][msg.sender] = Vote({
            hasVoted: true,
            favorBuyer: favorBuyer,
            reasoning: reasoning,
            votedAt: block.timestamp
        });

        // Update vote counts
        if (favorBuyer) {
            dispute.votesForBuyer++;
        } else {
            dispute.votesForSeller++;
        }

        arbitrators[msg.sender].totalVotes++;

        emit VoteCast(disputeId, msg.sender, favorBuyer);

        // Check if we have enough votes to resolve
        if (dispute.votesForBuyer + dispute.votesForSeller >= votesRequired) {
            _resolveDispute(disputeId);
        }
    }

    /**
     * @notice Force resolve after voting deadline (if not enough votes)
     */
    function forceResolve(uint256 disputeId) external {
        Dispute storage dispute = disputes[disputeId];

        if (dispute.status != DisputeStatus.Voting) revert InvalidStatus();
        if (block.timestamp <= dispute.votingDeadline) revert VotingNotClosed();

        // If we have at least one vote, resolve based on that
        if (dispute.votesForBuyer + dispute.votesForSeller > 0) {
            _resolveDispute(disputeId);
        } else {
            // Escalate to admin if no votes
            _escalateDispute(disputeId);
        }
    }

    /**
     * @notice Admin resolution (for escalated disputes)
     */
    function adminResolve(
        uint256 disputeId,
        bool favorBuyer
    ) external onlyOwner {
        Dispute storage dispute = disputes[disputeId];

        if (dispute.status != DisputeStatus.Escalated && dispute.tier != DisputeTier.Admin) {
            revert InvalidStatus();
        }

        dispute.finalDecision = favorBuyer;
        dispute.status = DisputeStatus.Resolved;
        dispute.resolvedAt = block.timestamp;

        // Call escrow contract to execute resolution
        _executeResolution(disputeId, favorBuyer);

        emit DisputeResolved(disputeId, favorBuyer);
    }

    // ============ Internal Functions ============

    function _selectArbitrators(uint256 disputeId) internal {
        Dispute storage dispute = disputes[disputeId];

        // Get list of eligible arbitrators
        address[] memory eligible = _getEligibleArbitrators(dispute.buyer, dispute.seller);

        if (eligible.length < 3) revert InsufficientArbitrators();

        // Pseudo-random selection (in production, use VRF)
        uint256 seed = uint256(keccak256(abi.encodePacked(
            block.timestamp,
            block.prevrandao,
            disputeId
        )));

        for (uint256 i = 0; i < 3; i++) {
            uint256 index = (seed >> (i * 8)) % eligible.length;
            dispute.selectedArbitrators[i] = eligible[index];

            // Swap to avoid selecting same arbitrator twice
            eligible[index] = eligible[eligible.length - 1 - i];
        }

        emit ArbitratorsSelected(disputeId, dispute.selectedArbitrators);
    }

    function _getEligibleArbitrators(
        address buyer,
        address seller
    ) internal view returns (address[] memory) {
        uint256 count = 0;

        // Count eligible
        for (uint256 i = 0; i < arbitratorList.length; i++) {
            address arb = arbitratorList[i];
            if (_isEligibleArbitrator(arb, buyer, seller)) {
                count++;
            }
        }

        // Build array
        address[] memory eligible = new address[](count);
        uint256 index = 0;

        for (uint256 i = 0; i < arbitratorList.length; i++) {
            address arb = arbitratorList[i];
            if (_isEligibleArbitrator(arb, buyer, seller)) {
                eligible[index] = arb;
                index++;
            }
        }

        return eligible;
    }

    function _isEligibleArbitrator(
        address arb,
        address buyer,
        address seller
    ) internal view returns (bool) {
        Arbitrator storage arbitrator = arbitrators[arb];

        // Must be active with sufficient stake
        if (!arbitrator.isActive) return false;
        if (arbitrator.stake < minArbitratorStake) return false;

        // Cannot be a party to the dispute
        if (arb == buyer || arb == seller) return false;

        // Check accuracy (if they have voted before)
        if (arbitrator.totalVotes > 0) {
            uint256 accuracy = (arbitrator.correctVotes * 10000) / arbitrator.totalVotes;
            if (accuracy < 5000) return false; // < 50% accuracy
        }

        return true;
    }

    function _isSelectedArbitrator(uint256 disputeId, address arb) internal view returns (bool) {
        Dispute storage dispute = disputes[disputeId];
        return dispute.selectedArbitrators[0] == arb ||
               dispute.selectedArbitrators[1] == arb ||
               dispute.selectedArbitrators[2] == arb;
    }

    function _resolveDispute(uint256 disputeId) internal {
        Dispute storage dispute = disputes[disputeId];

        bool buyerWins = dispute.votesForBuyer > dispute.votesForSeller;
        dispute.finalDecision = buyerWins;
        dispute.status = DisputeStatus.Resolved;
        dispute.resolvedAt = block.timestamp;

        // Reward/penalize arbitrators
        _processArbitratorOutcomes(disputeId, buyerWins);

        // Execute resolution in escrow contract
        _executeResolution(disputeId, buyerWins);

        emit DisputeResolved(disputeId, buyerWins);
    }

    function _processArbitratorOutcomes(uint256 disputeId, bool buyerWins) internal {
        Dispute storage dispute = disputes[disputeId];
        uint256 rewardPerArb = (dispute.amount * arbitratorRewardBps) / 10000 / 3;

        for (uint256 i = 0; i < 3; i++) {
            address arb = dispute.selectedArbitrators[i];
            if (arb == address(0)) continue;

            Vote storage vote = disputeVotes[disputeId][arb];
            if (!vote.hasVoted) continue;

            Arbitrator storage arbitrator = arbitrators[arb];
            arbitrator.disputesResolved++;

            if (vote.favorBuyer == buyerWins) {
                // Correct vote - reward
                arbitrator.correctVotes++;
                arbitrator.totalEarned += rewardPerArb;
                usdc.safeTransfer(arb, rewardPerArb);
                emit ArbitratorRewarded(arb, rewardPerArb);
            } else {
                // Wrong vote - accuracy penalty
                emit ArbitratorPenalized(arb, 500); // 5% accuracy drop logged
            }
        }
    }

    function _executeResolution(uint256 disputeId, bool buyerWins) internal {
        Dispute storage dispute = disputes[disputeId];

        // Call escrow contract to execute resolution
        // This would call resolveDispute on P2PEscrowV2
        (bool success,) = escrowContract.call(
            abi.encodeWithSignature(
                "resolveDispute(bytes32,bool,bool)",
                dispute.escrowOrderId,
                !buyerWins, // favorSender = seller wins
                true        // slashLoser
            )
        );

        require(success, "Resolution execution failed");
    }

    function _escalateDispute(uint256 disputeId) internal {
        Dispute storage dispute = disputes[disputeId];
        dispute.status = DisputeStatus.Escalated;
        dispute.tier = DisputeTier.Admin;

        emit DisputeEscalated(disputeId, DisputeTier.Admin);
    }

    // ============ Admin Functions ============

    function setEscrowContract(address _escrow) external onlyOwner {
        escrowContract = _escrow;
    }

    function setMinArbitratorStake(uint256 _stake) external onlyOwner {
        minArbitratorStake = _stake;
    }

    function setVotingPeriod(uint256 _period) external onlyOwner {
        votingPeriod = _period;
    }

    // ============ View Functions ============

    function getDispute(uint256 disputeId) external view returns (Dispute memory) {
        return disputes[disputeId];
    }

    function getArbitrator(address arb) external view returns (Arbitrator memory) {
        return arbitrators[arb];
    }

    function getArbitratorAccuracy(address arb) external view returns (uint256) {
        Arbitrator storage arbitrator = arbitrators[arb];
        if (arbitrator.totalVotes == 0) return 10000; // 100% if no votes
        return (arbitrator.correctVotes * 10000) / arbitrator.totalVotes;
    }

    function getVote(uint256 disputeId, address arb) external view returns (Vote memory) {
        return disputeVotes[disputeId][arb];
    }

    function getActiveArbitratorCount() external view returns (uint256) {
        uint256 count = 0;
        for (uint256 i = 0; i < arbitratorList.length; i++) {
            if (arbitrators[arbitratorList[i]].isActive) {
                count++;
            }
        }
        return count;
    }
}

contracts/P2PEscrow.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title P2PEscrow
 * @notice P2P Escrow contract for USDC-to-Fiat settlements
 * @dev Handles escrow creation, release, refunds, and disputes for P2P orders
 * 
 * Flow:
 * 1. Seller locks USDC in escrow (createEscrow)
 * 2. Buyer pays fiat off-chain
 * 3. Seller confirms fiat receipt (releaseEscrow) -> USDC goes to buyer
 * 4. If dispute, arbitrator resolves (resolveDispute)
 */
contract P2PEscrow is ReentrancyGuard, Ownable {
    using SafeERC20 for IERC20;

    // USDC token address (Arc uses USDC for gas and value)
    IERC20 public immutable usdc;

    // Platform fee (basis points, 100 = 1%)
    uint256 public platformFeeBps = 50; // 0.5%

    // Dispute resolution timeout (24 hours)
    uint256 public disputeTimeout = 24 hours;

    // Escrow statuses
    enum EscrowStatus {
        None,
        Pending,    // Created, waiting for fiat payment
        Locked,     // USDC locked, fiat being sent
        Released,   // USDC released to recipient
        Refunded,   // USDC refunded to sender
        Disputed    // Under dispute resolution
    }

    // Escrow data structure
    struct Escrow {
        address sender;         // Who locked the USDC (seller in sell order)
        address recipient;      // Who receives USDC (buyer in sell order)
        uint256 amount;         // USDC amount (6 decimals)
        uint256 fee;            // Platform fee
        EscrowStatus status;
        uint256 createdAt;
        uint256 expiresAt;
        uint256 disputedAt;
        string disputeReason;
    }

    // Mapping from orderId to Escrow
    mapping(bytes32 => Escrow) public escrows;

    // Arbitrator address (for dispute resolution)
    address public arbitrator;

    // Events
    event EscrowCreated(
        bytes32 indexed orderId,
        address indexed sender,
        address indexed recipient,
        uint256 amount,
        uint256 expiresAt
    );

    event EscrowReleased(
        bytes32 indexed orderId,
        address indexed recipient,
        uint256 amount
    );

    event EscrowRefunded(
        bytes32 indexed orderId,
        address indexed sender,
        uint256 amount
    );

    event DisputeRaised(
        bytes32 indexed orderId,
        address indexed raiser,
        string reason
    );

    event DisputeResolved(
        bytes32 indexed orderId,
        address indexed winner,
        uint256 amount
    );

    event ArbitratorUpdated(address indexed oldArbitrator, address indexed newArbitrator);
    event PlatformFeeUpdated(uint256 oldFee, uint256 newFee);

    // Errors
    error EscrowAlreadyExists();
    error EscrowNotFound();
    error InvalidStatus();
    error NotAuthorized();
    error EscrowExpired();
    error EscrowNotExpired();
    error InvalidAmount();
    error TransferFailed();

    constructor(address _usdc, address _arbitrator) Ownable(msg.sender) {
        usdc = IERC20(_usdc);
        arbitrator = _arbitrator;
    }

    /**
     * @notice Create a new escrow by locking USDC
     * @param orderId Unique order identifier
     * @param amount USDC amount to lock (6 decimals)
     * @param recipient Address that will receive USDC on release
     * @param expiresAt Unix timestamp when escrow expires
     * @return escrowId The order ID (same as input)
     */
    function createEscrow(
        bytes32 orderId,
        uint256 amount,
        address recipient,
        uint256 expiresAt
    ) external nonReentrant returns (bytes32) {
        if (escrows[orderId].status != EscrowStatus.None) {
            revert EscrowAlreadyExists();
        }
        if (amount == 0) {
            revert InvalidAmount();
        }
        if (expiresAt <= block.timestamp) {
            revert EscrowExpired();
        }

        // Calculate fee
        uint256 fee = (amount * platformFeeBps) / 10000;
        uint256 totalAmount = amount + fee;

        // Transfer USDC from sender to this contract
        usdc.safeTransferFrom(msg.sender, address(this), totalAmount);

        // Create escrow
        escrows[orderId] = Escrow({
            sender: msg.sender,
            recipient: recipient,
            amount: amount,
            fee: fee,
            status: EscrowStatus.Locked,
            createdAt: block.timestamp,
            expiresAt: expiresAt,
            disputedAt: 0,
            disputeReason: ""
        });

        emit EscrowCreated(orderId, msg.sender, recipient, amount, expiresAt);

        return orderId;
    }

    /**
     * @notice Release escrow to recipient (seller confirms fiat received)
     * @param orderId The order ID
     * @return success True if release successful
     */
    function releaseEscrow(bytes32 orderId) external nonReentrant returns (bool) {
        Escrow storage escrow = escrows[orderId];

        if (escrow.status == EscrowStatus.None) {
            revert EscrowNotFound();
        }
        if (escrow.status != EscrowStatus.Locked) {
            revert InvalidStatus();
        }
        // Only sender can release (they're confirming they received fiat)
        if (msg.sender != escrow.sender) {
            revert NotAuthorized();
        }

        escrow.status = EscrowStatus.Released;

        // Transfer USDC to recipient
        usdc.safeTransfer(escrow.recipient, escrow.amount);

        // Transfer fee to platform
        if (escrow.fee > 0) {
            usdc.safeTransfer(owner(), escrow.fee);
        }

        emit EscrowReleased(orderId, escrow.recipient, escrow.amount);

        return true;
    }

    /**
     * @notice Refund escrow to sender (order cancelled or expired)
     * @param orderId The order ID
     * @return success True if refund successful
     */
    function refundEscrow(bytes32 orderId) external nonReentrant returns (bool) {
        Escrow storage escrow = escrows[orderId];

        if (escrow.status == EscrowStatus.None) {
            revert EscrowNotFound();
        }
        if (escrow.status != EscrowStatus.Locked) {
            revert InvalidStatus();
        }

        // Sender can refund anytime, recipient can refund after expiry
        bool isSender = msg.sender == escrow.sender;
        bool isRecipientAfterExpiry = msg.sender == escrow.recipient && block.timestamp > escrow.expiresAt;

        if (!isSender && !isRecipientAfterExpiry) {
            revert NotAuthorized();
        }

        escrow.status = EscrowStatus.Refunded;

        // Refund USDC + fee to sender
        usdc.safeTransfer(escrow.sender, escrow.amount + escrow.fee);

        emit EscrowRefunded(orderId, escrow.sender, escrow.amount);

        return true;
    }

    /**
     * @notice Raise a dispute for an escrow
     * @param orderId The order ID
     * @param reason Dispute reason
     * @return disputeId The order ID
     */
    function raiseDispute(
        bytes32 orderId,
        string calldata reason
    ) external nonReentrant returns (bytes32) {
        Escrow storage escrow = escrows[orderId];

        if (escrow.status == EscrowStatus.None) {
            revert EscrowNotFound();
        }
        if (escrow.status != EscrowStatus.Locked) {
            revert InvalidStatus();
        }
        // Only sender or recipient can raise dispute
        if (msg.sender != escrow.sender && msg.sender != escrow.recipient) {
            revert NotAuthorized();
        }

        escrow.status = EscrowStatus.Disputed;
        escrow.disputedAt = block.timestamp;
        escrow.disputeReason = reason;

        emit DisputeRaised(orderId, msg.sender, reason);

        return orderId;
    }

    /**
     * @notice Resolve a dispute (arbitrator only)
     * @param orderId The order ID
     * @param releaseToRecipient True to release to recipient, false to refund sender
     */
    function resolveDispute(
        bytes32 orderId,
        bool releaseToRecipient
    ) external nonReentrant {
        if (msg.sender != arbitrator) {
            revert NotAuthorized();
        }

        Escrow storage escrow = escrows[orderId];

        if (escrow.status != EscrowStatus.Disputed) {
            revert InvalidStatus();
        }

        if (releaseToRecipient) {
            escrow.status = EscrowStatus.Released;
            usdc.safeTransfer(escrow.recipient, escrow.amount);
            usdc.safeTransfer(owner(), escrow.fee);
            emit DisputeResolved(orderId, escrow.recipient, escrow.amount);
        } else {
            escrow.status = EscrowStatus.Refunded;
            usdc.safeTransfer(escrow.sender, escrow.amount + escrow.fee);
            emit DisputeResolved(orderId, escrow.sender, escrow.amount);
        }
    }

    /**
     * @notice Get escrow details
     * @param orderId The order ID
     */
    function getEscrow(bytes32 orderId) external view returns (
        address sender,
        address recipient,
        uint256 amount,
        uint8 status,
        uint256 createdAt,
        uint256 expiresAt
    ) {
        Escrow storage escrow = escrows[orderId];
        return (
            escrow.sender,
            escrow.recipient,
            escrow.amount,
            uint8(escrow.status),
            escrow.createdAt,
            escrow.expiresAt
        );
    }

    // Admin functions

    function setArbitrator(address _arbitrator) external onlyOwner {
        emit ArbitratorUpdated(arbitrator, _arbitrator);
        arbitrator = _arbitrator;
    }

    function setPlatformFee(uint256 _feeBps) external onlyOwner {
        require(_feeBps <= 500, "Fee too high"); // Max 5%
        emit PlatformFeeUpdated(platformFeeBps, _feeBps);
        platformFeeBps = _feeBps;
    }

    function setDisputeTimeout(uint256 _timeout) external onlyOwner {
        disputeTimeout = _timeout;
    }
}

contracts/P2PEscrowV2.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";

/**
 * @title P2PEscrowV2
 * @notice Enhanced P2P Escrow with dynamic stakes, slashing, and fraud prevention
 * @dev Handles escrow creation, release, refunds, disputes, and stake management
 * 
 * New Features:
 * - Dynamic stake requirements based on risk score
 * - Stake slashing for bad actors
 * - Rate limiting to prevent abuse
 * - Emergency pause functionality
 * - Circuit breaker for volume limits
 */
contract P2PEscrowV2 is ReentrancyGuard, Ownable, Pausable {
    using SafeERC20 for IERC20;

    // ============ State Variables ============

    IERC20 public immutable usdc;

    // Platform settings
    uint256 public platformFeeBps = 50;          // 0.5%
    uint256 public baseStakeBps = 500;           // 5% base stake
    uint256 public disputeTimeout = 24 hours;
    uint256 public rateLimitSeconds = 60;        // 1 min between orders
    uint256 public maxDailyVolume = 1_000_000 * 1e6; // $1M daily limit

    // Tracking
    uint256 public dailyVolume;
    uint256 public lastVolumeReset;

    // ============ Enums ============

    enum EscrowStatus {
        None,
        Pending,
        Locked,
        Released,
        Refunded,
        Disputed
    }

    enum PaymentMethod {
        UPI
    }

    // ============ Structs ============

    struct Escrow {
        address sender;
        address recipient;
        uint256 amount;
        uint256 fee;
        uint256 stakeAmount;     // NEW: Stake locked for this escrow
        EscrowStatus status;
        uint256 createdAt;
        uint256 expiresAt;
        uint256 disputedAt;
        string disputeReason;
        bytes32 paymentProofHash; // NEW: Hash of UTR + amount + timestamp
    }

    struct StakeProfile {
        uint256 baseStake;           // Total stake deposited
        uint256 lockedStake;         // Stake locked in active escrows
        uint256 tradingLimit;        // Max daily trading volume
        uint256 lastTradeTime;       // Last trade timestamp
        uint256 completedTrades;     // Successful trade count
        uint256 disputesLost;        // Number of disputes lost
        bool isLP;                   // Is a liquidity provider
    }

    struct RiskMultipliers {
        uint16 low;      // 100 = 1.0x
        uint16 medium;   // 150 = 1.5x
        uint16 high;     // 200 = 2.0x
        uint16 critical; // 300 = 3.0x
    }

    // ============ Mappings ============

    mapping(bytes32 => Escrow) public escrows;
    mapping(address => StakeProfile) public stakes;
    mapping(PaymentMethod => uint8) public paymentMethodRisk;

    // ============ Immutables & Constants ============

    address public arbitrator;
    address public disputeDAO;     // NEW: DisputeDAO contract address
    RiskMultipliers public riskMultipliers;

    // Slashing percentages (basis points, 100 = 1%)
    uint256 public constant SLASH_ORDER_TIMEOUT = 2000;      // 20%
    uint256 public constant SLASH_FAKE_PROOF = 10000;        // 100%
    uint256 public constant SLASH_DISPUTE_LOST = 5000;       // 50%
    uint256 public constant SLASH_PAYMENT_REVERSAL = 20000;  // 200%
    uint256 public constant SLASH_LATE_RELEASE = 500;        // 5%

    // ============ Events ============

    event EscrowCreated(
        bytes32 indexed orderId,
        address indexed sender,
        address indexed recipient,
        uint256 amount,
        uint256 stakeAmount,
        uint256 expiresAt
    );

    event EscrowReleased(
        bytes32 indexed orderId,
        address indexed recipient,
        uint256 amount
    );

    event EscrowRefunded(
        bytes32 indexed orderId,
        address indexed sender,
        uint256 amount
    );

    event DisputeRaised(
        bytes32 indexed orderId,
        address indexed raiser,
        string reason
    );

    event DisputeResolved(
        bytes32 indexed orderId,
        address indexed winner,
        uint256 amount
    );

    event StakeDeposited(address indexed user, uint256 amount);
    event StakeWithdrawn(address indexed user, uint256 amount);
    event StakeSlashed(address indexed user, uint256 amount, string reason);
    event PaymentProofSubmitted(bytes32 indexed orderId, bytes32 proofHash);
    event DailyVolumeReset(uint256 oldVolume, uint256 timestamp);

    // ============ Errors ============

    error EscrowAlreadyExists();
    error EscrowNotFound();
    error InvalidStatus();
    error NotAuthorized();
    error EscrowExpired();
    error EscrowNotExpired();
    error InvalidAmount();
    error TransferFailed();
    error InsufficientStake();
    error RateLimitExceeded();
    error DailyVolumeLimitExceeded();
    error OrderBlocked();

    // ============ Modifiers ============

    modifier rateLimited() {
        if (block.timestamp - stakes[msg.sender].lastTradeTime < rateLimitSeconds) {
            revert RateLimitExceeded();
        }
        _;
    }

    modifier volumeLimited(uint256 amount) {
        _resetDailyVolumeIfNeeded();
        if (dailyVolume + amount > maxDailyVolume) {
            revert DailyVolumeLimitExceeded();
        }
        _;
    }

    // ============ Constructor ============

    constructor(
        address _usdc,
        address _arbitrator
    ) Ownable(msg.sender) {
        usdc = IERC20(_usdc);
        arbitrator = _arbitrator;
        lastVolumeReset = block.timestamp;

        // Initialize risk multipliers
        riskMultipliers = RiskMultipliers({
            low: 100,
            medium: 150,
            high: 200,
            critical: 300
        });

        // Initialize payment method risk (UPI only)
        paymentMethodRisk[PaymentMethod.UPI] = 20;
    }

    // ============ Stake Management ============

    /**
     * @notice Deposit stake to enable trading
     * @param amount Amount of USDC to stake
     */
    function depositStake(uint256 amount) external nonReentrant whenNotPaused {
        if (amount == 0) revert InvalidAmount();

        usdc.safeTransferFrom(msg.sender, address(this), amount);
        stakes[msg.sender].baseStake += amount;

        emit StakeDeposited(msg.sender, amount);
    }

    /**
     * @notice Withdraw available stake (not locked in escrows)
     * @param amount Amount to withdraw
     */
    function withdrawStake(uint256 amount) external nonReentrant {
        StakeProfile storage profile = stakes[msg.sender];
        uint256 availableStake = profile.baseStake - profile.lockedStake;

        if (amount > availableStake) revert InsufficientStake();

        profile.baseStake -= amount;
        usdc.safeTransfer(msg.sender, amount);

        emit StakeWithdrawn(msg.sender, amount);
    }

    /**
     * @notice Calculate required stake for an order
     * @param user User address
     * @param amount Order amount
     * @param riskScore Risk score (0-100)
     */
    function calculateRequiredStake(
        address user,
        uint256 amount,
        uint8 riskScore
    ) public view returns (uint256) {
        // Base stake: 5% of order amount
        uint256 baseRequired = (amount * baseStakeBps) / 10000;

        // Get risk multiplier
        uint256 multiplier;
        if (riskScore < 20) {
            multiplier = riskMultipliers.low;
        } else if (riskScore < 40) {
            multiplier = riskMultipliers.medium;
        } else if (riskScore < 60) {
            multiplier = riskMultipliers.high;
        } else {
            multiplier = riskMultipliers.critical;
        }

        // Trust discount: More trades = lower stake (max 20% discount)
        uint256 trustDiscount = 100;
        if (stakes[user].completedTrades > 50) {
            trustDiscount = 80;
        } else if (stakes[user].completedTrades > 20) {
            trustDiscount = 90;
        }

        return (baseRequired * multiplier * trustDiscount) / 10000;
    }

    // ============ Escrow Functions ============

    /**
     * @notice Create a new escrow with stake
     */
    function createEscrow(
        bytes32 orderId,
        uint256 amount,
        address recipient,
        uint256 expiresAt,
        uint8 riskScore
    ) external nonReentrant whenNotPaused rateLimited volumeLimited(amount) returns (bytes32) {
        if (escrows[orderId].status != EscrowStatus.None) {
            revert EscrowAlreadyExists();
        }
        if (amount == 0) revert InvalidAmount();
        if (expiresAt <= block.timestamp) revert EscrowExpired();
        if (riskScore >= 70) revert OrderBlocked();

        // Calculate stake requirement
        uint256 requiredStake = calculateRequiredStake(msg.sender, amount, riskScore);
        StakeProfile storage profile = stakes[msg.sender];
        uint256 availableStake = profile.baseStake - profile.lockedStake;

        if (availableStake < requiredStake) revert InsufficientStake();

        // Calculate fee
        uint256 fee = (amount * platformFeeBps) / 10000;
        uint256 totalAmount = amount + fee;

        // Transfer USDC
        usdc.safeTransferFrom(msg.sender, address(this), totalAmount);

        // Lock stake
        profile.lockedStake += requiredStake;
        profile.lastTradeTime = block.timestamp;

        // Update daily volume
        dailyVolume += amount;

        // Create escrow
        escrows[orderId] = Escrow({
            sender: msg.sender,
            recipient: recipient,
            amount: amount,
            fee: fee,
            stakeAmount: requiredStake,
            status: EscrowStatus.Locked,
            createdAt: block.timestamp,
            expiresAt: expiresAt,
            disputedAt: 0,
            disputeReason: "",
            paymentProofHash: bytes32(0)
        });

        emit EscrowCreated(orderId, msg.sender, recipient, amount, requiredStake, expiresAt);

        return orderId;
    }

    /**
     * @notice Submit payment proof hash
     */
    function submitPaymentProof(
        bytes32 orderId,
        bytes32 proofHash
    ) external nonReentrant {
        Escrow storage escrow = escrows[orderId];

        if (escrow.status != EscrowStatus.Locked) revert InvalidStatus();
        if (msg.sender != escrow.recipient) revert NotAuthorized();

        escrow.paymentProofHash = proofHash;

        emit PaymentProofSubmitted(orderId, proofHash);
    }

    /**
     * @notice Release escrow to recipient
     */
    function releaseEscrow(bytes32 orderId) external nonReentrant returns (bool) {
        Escrow storage escrow = escrows[orderId];

        if (escrow.status != EscrowStatus.Locked) revert InvalidStatus();
        if (msg.sender != escrow.sender) revert NotAuthorized();

        // Check for late release (>30 min)
        if (block.timestamp > escrow.createdAt + 30 minutes) {
            _slashStake(escrow.sender, escrow.stakeAmount, SLASH_LATE_RELEASE, "Late release");
        }

        // Update status
        escrow.status = EscrowStatus.Released;

        // Return stake
        stakes[escrow.sender].lockedStake -= escrow.stakeAmount;
        stakes[escrow.sender].completedTrades += 1;

        // Transfer amounts
        usdc.safeTransfer(escrow.recipient, escrow.amount);
        usdc.safeTransfer(owner(), escrow.fee);

        emit EscrowReleased(orderId, escrow.recipient, escrow.amount);

        return true;
    }

    /**
     * @notice Refund escrow to sender (after expiry)
     */
    function refundEscrow(bytes32 orderId) external nonReentrant returns (bool) {
        Escrow storage escrow = escrows[orderId];

        if (escrow.status != EscrowStatus.Locked) revert InvalidStatus();
        if (block.timestamp < escrow.expiresAt) revert EscrowNotExpired();

        // Slash recipient stake for timeout
        if (stakes[escrow.recipient].baseStake > 0) {
            _slashStake(escrow.recipient, stakes[escrow.recipient].baseStake, SLASH_ORDER_TIMEOUT, "Order timeout");
        }

        escrow.status = EscrowStatus.Refunded;

        // Return sender's stake
        stakes[escrow.sender].lockedStake -= escrow.stakeAmount;

        // Refund
        usdc.safeTransfer(escrow.sender, escrow.amount + escrow.fee);

        emit EscrowRefunded(orderId, escrow.sender, escrow.amount);

        return true;
    }

    /**
     * @notice Raise a dispute
     */
    function raiseDispute(
        bytes32 orderId,
        string calldata reason
    ) external nonReentrant returns (bytes32) {
        Escrow storage escrow = escrows[orderId];

        if (escrow.status != EscrowStatus.Locked) revert InvalidStatus();
        if (msg.sender != escrow.sender && msg.sender != escrow.recipient) {
            revert NotAuthorized();
        }

        escrow.status = EscrowStatus.Disputed;
        escrow.disputedAt = block.timestamp;
        escrow.disputeReason = reason;

        emit DisputeRaised(orderId, msg.sender, reason);

        return orderId;
    }

    /**
     * @notice Resolve dispute (arbitrator or DisputeDAO only)
     */
    function resolveDispute(
        bytes32 orderId,
        bool favorSender,
        bool slashLoser
    ) external nonReentrant {
        if (msg.sender != arbitrator && msg.sender != disputeDAO) {
            revert NotAuthorized();
        }

        Escrow storage escrow = escrows[orderId];
        if (escrow.status != EscrowStatus.Disputed) revert InvalidStatus();

        address winner = favorSender ? escrow.sender : escrow.recipient;
        address loser = favorSender ? escrow.recipient : escrow.sender;

        // Slash loser's stake if requested
        if (slashLoser && stakes[loser].baseStake > 0) {
            _slashStake(loser, stakes[loser].baseStake, SLASH_DISPUTE_LOST, "Dispute lost");
            stakes[loser].disputesLost += 1;
        }

        // Return winner's locked stake
        if (winner == escrow.sender) {
            stakes[escrow.sender].lockedStake -= escrow.stakeAmount;
        }

        // Transfer funds to winner
        if (favorSender) {
            escrow.status = EscrowStatus.Refunded;
            usdc.safeTransfer(escrow.sender, escrow.amount + escrow.fee);
        } else {
            escrow.status = EscrowStatus.Released;
            usdc.safeTransfer(escrow.recipient, escrow.amount);
            usdc.safeTransfer(owner(), escrow.fee);
        }

        emit DisputeResolved(orderId, winner, escrow.amount);
    }

    // ============ Internal Functions ============

    function _slashStake(
        address user,
        uint256 stakeAmount,
        uint256 slashBps,
        string memory reason
    ) internal {
        uint256 slashAmount = (stakeAmount * slashBps) / 10000;

        if (slashAmount > stakes[user].baseStake) {
            slashAmount = stakes[user].baseStake;
        }

        stakes[user].baseStake -= slashAmount;

        // Transfer slashed amount to treasury
        usdc.safeTransfer(owner(), slashAmount);

        emit StakeSlashed(user, slashAmount, reason);
    }

    function _resetDailyVolumeIfNeeded() internal {
        if (block.timestamp > lastVolumeReset + 1 days) {
            emit DailyVolumeReset(dailyVolume, block.timestamp);
            dailyVolume = 0;
            lastVolumeReset = block.timestamp;
        }
    }

    // ============ Admin Functions ============

    function setArbitrator(address _arbitrator) external onlyOwner {
        arbitrator = _arbitrator;
    }

    function setDisputeDAO(address _disputeDAO) external onlyOwner {
        disputeDAO = _disputeDAO;
    }

    function setPlatformFee(uint256 _feeBps) external onlyOwner {
        require(_feeBps <= 500, "Fee too high"); // Max 5%
        platformFeeBps = _feeBps;
    }

    function setBaseStake(uint256 _stakeBps) external onlyOwner {
        require(_stakeBps <= 2000, "Stake too high"); // Max 20%
        baseStakeBps = _stakeBps;
    }

    function setMaxDailyVolume(uint256 _volume) external onlyOwner {
        maxDailyVolume = _volume;
    }

    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }

    // ============ View Functions ============

    function getEscrow(bytes32 orderId) external view returns (Escrow memory) {
        return escrows[orderId];
    }

    function getStakeProfile(address user) external view returns (StakeProfile memory) {
        return stakes[user];
    }

    function getAvailableStake(address user) external view returns (uint256) {
        return stakes[user].baseStake - stakes[user].lockedStake;
    }
}

